CREATE TABLE IF NOT EXISTS `#__otc_buy_transactions` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `companyid` int(11) NOT NULL,  `memberid` int(11) NOT NULL,  `num_shares` int(5) NOT NULL,  `share_price` int(5) NOT NULL,  `bidding_price` int(5) NOT NULL,  `security_tax` int(5) NOT NULL,  `transaction_fee` int(5) NOT NULL DEFAULT 0,  `confirmed` int(1) NOT NULL DEFAULT 0,  `pending` int(1) NOT NULL DEFAULT 1,  `expiry_date` DATE NOT NULL,  `ts` timestamp NOT NULL default CURRENT_TIMESTAMP,   PRIMARY KEY  (`id`),   FOREIGN KEY (`companyid`) REFERENCES `#__otc_companies`(`id`),   FOREIGN KEY (`memberid`) REFERENCES `#__otc_members`(`id`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__otc_sell_transactions` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `companyid` int(11) NOT NULL,  `memberid` int(11) NOT NULL,  `num_shares` int(5) NOT NULL,  `selling_price` int(5) NOT NULL,  `security_tax` int(5) NOT NULL DEFAULT 0,  `transaction_fee` int(5) NOT NULL,  `confirmed` int(1) NOT NULL DEFAULT 0,  `pending` int(1) NOT NULL DEFAULT 1,  `expiry_date` DATE NOT NULL,  `ts` timestamp NOT NULL default CURRENT_TIMESTAMP,   PRIMARY KEY  (`id`),   FOREIGN KEY (`companyid`) REFERENCES `#__otc_companies`(`id`),   FOREIGN KEY (`memberid`) REFERENCES `#__otc_members`(`id`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;